export default function Option({ question, dispatch, answer }) {
  return (
    <div className="options">
      {question.options.map((option) => (
        <button
          className="btn btn-options"
          key={option}
          onClick={() => dispatch({ type: "newAnswer" })}
        >
          {option}
        </button>
      ))}
    </div>
  );
}
